import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ViewDocDetail.css';
import axios from 'axios';

export default function ViewDocDetail() {
  const { id } = useParams();
  const [doctor, setDoctor] = useState(null);

  useEffect(() => {
    // Send GET request to the API
    axios
      .get(`http://localhost:3000/api/doctor/details/${id}`)
      .then((response) => {
        // Handle the response from the API
        setDoctor(response.data.doctor);
      })
      .catch((error) => {
        // Handle any error that occurs during the request
        console.error('API Error:', error);
      });
  }, [id]);

  return (
    <div className="container">
      {doctor ? (
        <div className="doctor-profile">
          <img src={doctor.avatar} alt="Doctor Profile" className="profile-picture" />
          <h2 className="doctor-name">{doctor.fullname}</h2>
          {/* Render additional doctor information here */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
