import React, { useState } from 'react';
import axios from 'axios';
import './AddAmountPage.css';

function AddAmountPage() {
  const [customerId, setCustomerId] = useState('');
  const [amount, setAmount] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleCustomerIdChange = (event) => {
    setCustomerId(event.target.value);
  };

  const handleAmountChange = (event) => {
    setAmount(event.target.value);
  };

  const handleAddAmount = () => {
    setShowConfirmation(true);
  };

  const handleConfirm = () => {
    const data = {
      customerId,
      amount,
    };

    axios.post('http://localhost:3000/api/addAmount', data)
      .then((response) => {
        console.log(response.data);
        setCustomerId('');
        setAmount('');
        setShowConfirmation(false);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleCancel = () => {
    setCustomerId('');
    setAmount('');
    setShowConfirmation(false);
  };

  return (
    <div className="add-amount-page">
      <h2>Add Amount</h2>
      <div className="input-container">
        <label>Customer ID:</label>
        <input type="text" value={customerId} onChange={handleCustomerIdChange} />
      </div>
      <div className="input-container">
        <label>Amount:</label>
        <input type="number" value={amount} onChange={handleAmountChange} />
      </div>
      <button className="add-button" onClick={handleAddAmount}>Add</button>

      {showConfirmation && (
        <div className="confirmation-modal">
          <div className="confirmation-modal-content">
            <p>Are you sure you want to add the amount?</p>
            <div className="confirmation-buttons">
              <button className="confirm-button" onClick={handleConfirm}>Yes</button>
              <button className="cancel-button" onClick={handleCancel}>No</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AddAmountPage;
