import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './CustomerViewBooking.css';
import ViewBookingDetail from '../ViewBookingDetail/ViewBookingDetail';

function CustomerViewBooking() {
  const { id } = useParams();
  // Sử dụng useState để lưu trữ danh sách appointments
  const [appointments, setAppointments] = useState([]);

  // Sử dụng useState để lưu trữ appointment được chọn
  const [selectedAppointment, setSelectedAppointment] = useState(null);

  useEffect(() => {
    // Send GET request to fetch appointments
    axios
      .get(`http://localhost:3000/api/appointment/appointmentcustomer?customerID=${id}`)
      .then((response) => {
        // Handle the response from the API
        const appointmentsData = response.data.appointments;
        const appointmentPromises = appointmentsData.map(appointment => {
          // Gửi yêu cầu GET để lấy thông tin bác sĩ
          const doctorPromise = axios.get(`http://localhost:3000/api/account/doctor/details?id=${appointment.doctorID}`);
          // Gửi yêu cầu GET để lấy thông tin slot
          const slotPromise = axios.get(`http://localhost:3000/api/slot/getSlotbyDoctor?doctorId=${appointment.slotID}`);

          // Trả về một promise chứa kết quả từ cả hai yêu cầu GET
          return Promise.all([doctorPromise, slotPromise])
            .then(([doctorResponse, slotResponse]) => {
              appointment.doctor = doctorResponse.data.doctor;
              appointment.slot = slotResponse.data.slots[0];
              return appointment;
            });
        });

        // Chờ tất cả các yêu cầu GET hoàn thành trước khi cập nhật state
        Promise.all(appointmentPromises)
          .then(updatedAppointments => {
            setAppointments(updatedAppointments);
          })
          .catch((error) => {
            console.error('API Error:', error);
          });
      })
      .catch((error) => {
        // Handle any error that occurs during the request
        console.error('API Error:', error);
      });
  }, [id]);

  // Xử lý sự kiện xem chi tiết appointment
  const handleViewDetails = (appointment) => {
    setSelectedAppointment(appointment);
  };

  // Xử lý sự kiện đóng chi tiết appointment
  const handleCloseDetails = () => {
    setSelectedAppointment(null);
  };

  return (
    <div className="customer-view-booking">
      <h2>Danh sách đặt lịch</h2>
      <div className="booking-list">
        {appointments.map((appointment) => (
          <div className="booking-form" key={appointment.id}>
            <div className="booking-info">
              <span className="label">Tên liệu trình:</span>
              <span>{appointment.name}</span>
            </div>
            <div className="booking-info">
              <span className="label">Ngày điều trị:</span>
              <span>{appointment.treatmentDate}</span>
            </div>
            <div className="booking-info">
              <span className="label">Trạng thái:</span>
              <span>{appointment.status}</span>
            </div>
            <div className="booking-info">
              <span className="label">Bác sĩ:</span>
              <span>{appointment.doctor ? appointment.doctor.fullname : 'Unknown Doctor'}</span>
            </div>
            <div className="booking-info">
              <span className="label">Slot:</span>
              <span>{appointment.slot ? appointment.slot.name : 'Unknown Slot'}</span>
            </div>
            <div className="booking-actions">
              <button
                className="view-details-button"
                onClick={() => handleViewDetails(appointment)}
              >
                Xem chi tiết
              </button>
            </div>
          </div>
        ))}
      </div>
      {/* Hiển thị chi tiết appointment nếu appointment được chọn */}
      {selectedAppointment && (
        <ViewBookingDetail appointment={selectedAppointment} onClose={handleCloseDetails} />
      )}
    </div>
  );
}

export default CustomerViewBooking;
