import React, { useState, useEffect } from 'react';
import './CustomerViewDoctor.css';
import axios from 'axios';

const timeOptions = [
  { id: '8-9', label: '8h - 9h' },
  { id: '9-10', label: '9h - 10h' },
  { id: '10-11', label: '10h - 11h' },
  { id: '13-14', label: '13h - 14h' },
  { id: '15-16', label: '15h - 16h' },
];

function CustomerViewDoctor() {
  const [doctors, setDoctors] = useState([]);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [isConfirmationEnabled, setIsConfirmationEnabled] = useState(false);

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setSelectedDate(selectedDate);
    setIsConfirmationEnabled(selectedDate !== '' && selectedTime !== '');
  };

  const handleConfirmation = () => {
    // Perform any actions needed when the confirmation button is clicked
    console.log('Confirmation button clicked!');
    console.log('Selected Date:', selectedDate);
    console.log('Selected Time:', selectedTime);
  };

  return (
    <div className="customer-view-doctor">
      <div className="time-selection">
        <h2>Chọn thời gian trong ngày</h2>
        <div className="date-input">
          <label htmlFor="date">Ngày:</label>
          <input
            type="date"
            id="date"
            value={selectedDate}
            onChange={handleDateChange}
          />
        </div>
        <div className="time-options">
          {timeOptions.map((option) => (
            <button
              key={option.id}
              className={`time-option${selectedTime === option.id ? ' active' : ''}`}
              onClick={() => {
                setSelectedTime(option.id);
                setIsConfirmationEnabled(selectedDate !== '' && option.id !== '');
              }}
            >
              {option.label}
            </button>
          ))}
        </div>
        <button
          className="confirmation-button"
          disabled={!isConfirmationEnabled}
          onClick={handleConfirmation}
        >
          Xác nhận
        </button>
      </div>
    </div>
  );
}

export default CustomerViewDoctor;
