import React, { useState } from 'react';

function ResetAppointmentPage() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleAppointmentDateChange = (event) => {
    setAppointmentDate(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Kiểm tra các trường thông tin đã nhập
    if (name === '' || email === '' || appointmentDate === '') {
      setErrorMessage('Vui lòng nhập đầy đủ thông tin');
      return;
    }

    // Gửi yêu cầu đặt lại tái khám tới server (API) ở đây
    // ...

    // Xử lý phản hồi từ server (API) (nếu cần)
    // ...

    // Đặt lại các trường dữ liệu và hiển thị thông báo thành công
    setName('');
    setEmail('');
    setAppointmentDate('');
    setErrorMessage('');
    setSuccessMessage('Đặt lại lịch tái khám thành công');
  };

  return (
    <div>
      <h2>Đặt lại lịch tái khám</h2>

      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Họ và tên:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={handleEmailChange}
          />
        </div>
        <div>
          <label htmlFor="appointmentDate">Ngày tái khám:</label>
          <input
            type="date"
            id="appointmentDate"
            value={appointmentDate}
            onChange={handleAppointmentDateChange}
          />
        </div>
        <button type="submit">Đặt lại lịch tái khám</button>
      </form>
    </div>
  );
}

export default ResetAppointmentPage;
