import React, { useState, useEffect } from 'react';
import './PatientProfile.css';

const PatientProfile = () => {
  const customer = {
    name: 'Nguyễn Văn A',
    id: '123456789',
    gender: 'Nam',
    birthday: '01/01/1990',
    phone: '0123456789',
    email: 'example@example.com',
    records: [
      { id: 1, note: 'Ghi chú 1' },
      { id: 2, note: 'Ghi chú 2' },
      { id: 3, note: 'Ghi chú 3' },
      // ...
    ],
  };

  const pageSize = 5; // Kích thước trang
  const [currentPage, setCurrentPage] = useState(1);
  const [showPopup, setShowPopup] = useState(false);
  const [newRecordName, setNewRecordName] = useState('');
  const [currentRecords, setCurrentRecords] = useState([]);
  const totalPages = Math.ceil(customer.records.length / pageSize);

  useEffect(() => {
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const currentRecords = customer.records.slice(startIndex, endIndex);
    setCurrentRecords(currentRecords);
  }, [customer.records, currentPage]);

  const goToPreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const goToNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const goToFirstPage = () => {
    setCurrentPage(1);
  };

  const goToLastPage = () => {
    setCurrentPage(totalPages);
  };

  const openPopup = () => {
    setShowPopup(true);
  };

  const closePopup = () => {
    setShowPopup(false);
  };

  const handleAddRecord = () => {
    console.log('Thêm mới hồ sơ:', newRecordName);
    closePopup();
  };

  return (
    <div className="patient-profile">
      <h2>Thông tin bệnh nhân</h2>
      <div className="info-item">
        <span className="label">Họ tên:</span>
        <span className="value">{customer.name}</span>
      </div>
      <div className="info-item">
        <span className="label">Số chứng minh:</span>
        <span className="value">{customer.id}</span>
      </div>
      <div className="info-item">
        <span className="label">Giới tính:</span>
        <span className="value">{customer.gender}</span>
      </div>
      <div className="info-item">
        <span className="label">Ngày sinh:</span>
        <span className="value">{customer.birthday}</span>
      </div>
      <div className="info-item">
        <span className="label">Số điện thoại:</span>
        <span className="value">{customer.phone}</span>
      </div>
      <div className="info-item">
        <span className="label">Email:</span>
        <span className="value">{customer.email}</span>
      </div>
      <div className="actions">
        <button className="add-record-button" onClick={openPopup}>
          Thêm mới hồ sơ
```jsx
        </button>
      </div>
      <div className="records">
        <h3>Hồ sơ bệnh</h3>
        <ul>
          {currentRecords.map((record) => (
            <li key={record.id}>
              {record.note}
              <button className="view-details-button">Xem chi tiết</button>
            </li>
          ))}
        </ul>
      </div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Thêm mới hồ sơ</h3>
            <input
              type="text"
              placeholder="Tên hồ sơ"
              value={newRecordName}
              onChange={(e) => setNewRecordName(e.target.value)}
            />
            <div className="popup-buttons">
              <button className="popup-button" onClick={handleAddRecord}>
                Xác nhận
              </button>
              <button className="popup-button" onClick={closePopup}>
                Đóng
              </button>
            </div>
          </div>
        </div>
      )}
      <div className="pagination">
        <button
          className="pagination-button"
          onClick={goToFirstPage}
          disabled={currentPage === 1}
        >
          &lt;&lt;
        </button>
        <button
          className="pagination-button"
          onClick={goToPreviousPage}
          disabled={currentPage === 1}
        >
          &lt;
        </button>
        {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
          <button
            key={page}
            className={`pagination-button ${currentPage === page ? 'active' : ''}`}
            onClick={() => setCurrentPage(page)}
          >
            {page}
          </button>
        ))}
        <button
          className="pagination-button"
          onClick={goToNextPage}
          disabled={currentPage === totalPages}
        >
          &gt;
        </button>
        <button
          className="pagination-button"
          onClick={goToLastPage}
          disabled={currentPage === totalPages}
        >
          &gt;&gt;
        </button>
      </div>
    </div>
  );
};

export default PatientProfile;
